import styled from 'styled-components'
import {Link as linkR} from 'react-router-dom'
import{Link as LinkS} from 'react-scroll'





export const Nav = styled.nav`
background: #000;
height: 80px;
margin-top: -80px;
display: flex;
justify-content: center;
align-items: center;
font-size: 1rem;
position:sticky;
top: 0;
z-index: 10;

@media screen and (max-width: 960){
    transition: 0.8s all ease;
}
`
export const NavbarContainer = styled.div`
    display: flex;
    justify-content: space-between;
    height: 80px;
    z-index: 1;
    width: 100%;
    padding: 0 24px;
    max-width: 1100px 
`
export const NavLogo = styled.img`
    height: 80px;
    width: 80px;
    margin-bottom: 28px;
    justify-self: flex-start;
    display: flex;
    cursor: pointer;
    border-radius: 50px;
     padding: 5px; 
     padding: 0.2px;

     &:hover {
        transform: scale(1.52);
        transition: all 0.2s ease-in-out;
        cursor: pointer;
    }




` 


export const MobileIcon = styled.div`
    diplay: none;

    @media screen and (min-width: 0px) {
        diplay: block;
        position: absolute;
        top: 0;
        right: 0;
        transform: translate(-100%, 60%);
        font-size: 1.8rem;
        cursor: pointer;
        color: crimson;

    }
`;
export const NavMenu = styled.ul`
    display: flex;
     align-items: center;
    list-style: none;
    text-align: center;
    margin-right: -22px
     color: #fff;


    @media screen and (max-width: 768px) {
        display: none;
    }
` 
export const NavItem = styled.li`
    height: 25px;
`
export const NavLinks = styled(LinkS)`
    color: #fff;
    display: flex;
    align-items: center;
    text-decoration: none;
    padding: 0 1rem;
    hieght: 100%;
    cursor: pointer;

    &.active {
        border-bottom: 3px solid #01bf71;

    }

`;

export const NavBtn = styled.nav`
   display: flex;
   align-items: center;

   @media screen and (max-width: 768px){
       diplay: none;
   }
`
export const NavBtnLink = styled(linkR)`
  border-radius: 50px;
  background:  #01bf71;
  white-space: nowrap;
  padding: 10px 22px;
  color: #10606;
  font-size: 16px;
  outline: none;
  border: none; 
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  text-decoration: none;

  &:hover {
      transition: all 0.2s ease-in-out;
      background: #fff;
      color: #010606;
  }
`
